package sumofnumbers;

public class Claculatesum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int sum = 0;
	        for (int m = 1; m <= 10; m++) {
	            sum =sum + m;
	        }
	        System.out.println("The sum of the first 10 natural numbers is: " + sum);
	    }

}

